
from random import randint


def gen_reads(seq,r,n,filename):
    f = open(filename,'w')
    f.write(seq + '\n')
    for t in range(n):
        i = randint(0,len(seq)-1)
        if i + r <= len(seq):
            f.write(seq[i:i+r] + '\n')
        else:
            f.write(seq[i:] + seq[:r-(len(seq)-i)] + '\n')

gen_reads('',25,3200,'make_seq.out.self')






